import { Button } from "@mui/material";
import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { setDesde } from "../../actions/actions";

export default function Pagination (){
    const dispatch = useDispatch();
    const items = useSelector(state => state.items);
    const [desde, setdesde] = useState(0);
    const [hasta, sethasta] = useState(12); //cantidad de items que se muestran por pagina

    const next = () => {
      setdesde(desde + hasta);
        dispatch(
            setDesde(desde)
        )}
    const prev = () => {
        console.log('aca entra')
        setdesde(desde - hasta);
            dispatch(
                setDesde(desde)
            )}


    return (
        <div>
            <Button onClick={prev} disabled={desde<=0}>Anterior</Button>
            <Button onClick={next} disabled={items.length!==12} >Siguiente</Button>   {/*disabled={items.length!==12}*/}                  
        </div>
    )
}
