import { useEffect } from "react";
import {useDispatch, useSelector} from "react-redux";
import {getAllItems} from "../../actions/actions";
import BasicCard from "../Card/Card";
import { Grid,Box,Button } from "@mui/material";
// import { margin } from "@mui/system";
import { useState } from "react";
import Paginate from '../Paginate/Paginate.jsx'


export default function ContainerCards() {
const items = useSelector(state => state.items);
const [desde, setdesde] = useState(0); 
const [hasta, sethasta] = useState(12); 
// const dispatch = useDispatch();
// const { desde, hasta} = useSelector(state => state.paginado)

// useEffect(() => {
//   dispatch(getAllItems(desde,hasta))}
//   , [dispatch,desde,hasta]);

   const handleClick = () => {
    setdesde(desde + 12);
    console.log(desde)
   }
  
  const handleClick2 = () => {
   
      setdesde(desde - hasta);
  } 

return (


    <div>
   
     <Button onClick={handleClick2} disabled={desde<=0}>Anterior</Button>
     <Button onClick={handleClick}>Siguiente</Button> 
      {console.log(items.length)}
     {
        // <Paginate />
     }
     
      <Box 
        
        sx={{ 
        marginRight: 5,
        marginLeft: 5,
        marginTop: 5,
        marginBottom: 10,
      }}
      
      >

      
      <Grid container spacing={1} >


        {items.length ? (

          items.slice(desde,hasta).map(item => (    //map para recorrer el array de items
            <Grid item 
            xs={12} 
            sm={6} 
            md={4} 
            lg={2}
            >
              <BasicCard key={item.id} item={item} /> 
              </Grid>))
        ):(
          <Box sx={{
            hight: 'auto',
            marginTop: '17%',
            marginBottom: '17%',
            

          }}>
            
            <h1>Cargando...</h1>

            

           

          </Box>
        )
        }
      </Grid>
      </Box>
    </div>
)

}
