import { useEffect } from "react";
import {useDispatch, useSelector} from "react-redux";
import {getAllItems} from "../../actions/actions";
import BasicCard from "../Card/Card";
import { Grid,Box,Button } from "@mui/material";
// import { margin } from "@mui/system";
import { useState } from "react";


export default function ContainerCards() {
const items = useSelector(state => state.items);
const [desde, setdesde] = useState(0);
const [hasta, sethasta] = useState(8);
const dispatch = useDispatch();

useEffect(() => {
  dispatch(getAllItems(desde,hasta))}
  , [dispatch,desde,hasta]);

  const handleClick = () => {
    setdesde(desde + 8);
   
  }
  const handleClick2 = () => {
    setdesde(desde - 8);
   
  }

return (


    <div>
     <>
     <Button onClick={handleClick2}>Anterior</Button>
     <Button onClick={handleClick} disabled={items.length<7}>Siguiente</Button>
     </>
      <Box sx={{ 
        marginRight: 5,
        marginLeft: 5,
        marginTop: 5,
        marginBottom: 10,

      }}>

      
      <Grid container spacing={0.5}>


        {items.length ? (

          items.map(item => (    //map para recorrer el array de items
            <Grid item xs={12} sm={6} md={4} lg={3}>
              <BasicCard key={item.id} item={item} /> 
              </Grid>))
        ):(
          <Box sx={{
            hight: 'auto',
            marginTop: '17%',
            marginBottom: '17%'

          }}>
            
            <h1>Cargando...</h1>

            

           

          </Box>
        )
        }
      </Grid>
      </Box>
    </div>
)

}
