import React from "react";
import  AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import MenuIcon from "@material-ui/icons/Menu";
import InputBase from '@material-ui/core/InputBase';
import  Typography  from "@material-ui/core/Typography";
import SearchIcon from '@material-ui/icons/Search';
import Button from '@mui/material/Button';
import Container from '@material-ui/core/Container';
import {IconButton} from "@material-ui/core";
import  {makeStyles, alpha} from "@material-ui/core/styles";
import { useState} from "react";
import {useDispatch} from 'react-redux';
import { getAllProduct } from "../../actions/actions";
import { useAuth0 } from "@auth0/auth0-react";
import { LoginButton } from "../Login/Login";
import { Profile } from "../Profile/Profile";
import PermanentDrawerLeft from "../SideBar/SideBar"





const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1,
    },
    menuButton: {
      marginRight: theme.spacing(2),
    },
    title: {
      flexGrow: 1,
      display: 'none',
      [theme.breakpoints.up('sm')]: {
        display: 'block',
      },
    },
    search: {
      position: 'relative',
      borderRadius: theme.shape.borderRadius,
      backgroundColor: alpha(theme.palette.common.white, 0.15),
      '&:hover': {
        backgroundColor: alpha(theme.palette.common.white, 0.25),
      },
      marginLeft: 0,
      width: '100%',
      [theme.breakpoints.up('sm')]: {
        marginLeft: theme.spacing(1),
        width: 'auto',
      },
    },
    searchIcon: {
      padding: theme.spacing(0, 2),
      height: '100%',
      position: 'absolute',
      pointerEvents: 'none',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    },
    inputRoot: {
      color: 'inherit',
    },
    inputInput: {

        
      padding: theme.spacing(1, 1, 1, 0),
      // vertical padding + font size from searchIcon
      paddingLeft: `calc(1em + ${theme.spacing(4)}px)`,
      transition: theme.transitions.create('width'),
      width: '100%',
      [theme.breakpoints.up('sm')]: {
        width: '12ch',
        '&:focus': {
          width: '20ch',
        },
      },
    },
  }));


  export default function SearchAppBar() {
    const { isAuthenticated } = useAuth0();
    const classes = useStyles()
    const dispatch = useDispatch();
    const [name, setName] = useState("")
   
  
      const handleSearchBar = (e) => {
          setName(e.target.value)
      }
      
      // console.log(name)
  
      const handleSubmit =(e) => {
          e.preventDefault() // para que no refresque la pag si no hay info nueva, con el click.
          dispatch(getAllProduct(name))
      }
  


    return (
        <div className={classes.root}>
            <Container  className="contenedorNavCss" maxWidth="xl">
                <AppBar position="static">
                    <Toolbar>
                      <PermanentDrawerLeft />
                        <IconButton
                            edge="start"
                            className={classes.menuButton}
                            color="inherit"
                            aria-label="open drawer"
                        >
                          <MenuIcon />
                        </IconButton>
                        <Typography className={classes.title} variant="h6" noWrap>
                            Material-UI
                        </Typography>
                            <div className={classes.search}>
                                <div className={classes.searchIcon}>
                                <SearchIcon />
                                </div>
                                    <InputBase
                                        placeholder="Buscar..."
                                        classes={{
                                            root: classes.inputRoot,
                                            input: classes.inputInput,
                                        }}
                                        inputProps={{ 'aria-label': 'search' }}
                                        onChange={handleSearchBar}
                                    />
                                    <Button 
                                        variant="outlined" 
                                        className="Search" 
                                        type='submit' 
                                        onClick={(e) => handleSubmit(e)}
                                        > Buscar
                                    </Button>
                            </div>                          
                                {
                                    isAuthenticated ? (
                                        <div>
                                    <Profile />
                                </ div>
                                ) : (
                                    <LoginButton />
                                    
                                    )
  }
                    </Toolbar>
                </AppBar>
            </Container>
          
        </div>
   
    )}
