import { useEffect } from "react";
import {useDispatch, useSelector} from "react-redux";
import {getAllItems} from "../../actions/actions";
import BasicCard from "../Card/Card";
import { Grid,Box,Button } from "@mui/material";
// import { margin } from "@mui/system";
import { useState } from "react";
import Pagination from "../pagination/pagination";



export default function ContainerCards() {
const items = useSelector(state => state.items);
// const [desde, setdesde] = useState(0);
// const [hasta, sethasta] = useState(12);
const desde = useSelector(state => state.desde);
const limite = useSelector(state => state.limite);
const dispatch = useDispatch();
console.log(desde, limite)

useEffect(() => {
  dispatch(getAllItems(desde,limite))}
  , [dispatch,desde,limite]);

  // const handleClick = () => {
  //   setdesde(desde + 8);
   
  // }
  // const handleClick2 = () => {
   
  //     setdesde(desde - 8);
   
   
   
  // }

return (


    <div>
     {/* <>
     <Button onClick={handleClick2} disabled={desde<=0}>Anterior</Button>
     <Button onClick={handleClick} disabled={items.length!==12}>Siguiente</Button>
     {console.log(items.length)}
     </> */}
      <Pagination />
      <Box sx={{ 
        marginRight: 5,
        marginLeft: 5,
        marginTop: 5,
        marginBottom: 10,

      }}>

      
      <Grid container spacing={3}>


        {items.length ? (

          items.map(item => (    //map para recorrer el array de items
            <Grid item xs={12} sm={6} md={4} lg={2}>
              <BasicCard key={item.id} item={item} /> 
              </Grid>))
        ):(
          <Box sx={{
            hight: 'auto',
            marginTop: '17%',
            marginBottom: '17%'

          }}>
            
            <h1>Cargando...</h1>

            

           

          </Box>
        )
        }
      </Grid>
      </Box>
    </div>
)

}
